<?php header('Content-Type: application/json; charset=utf-8'); header('Cache-Control: no-cache, no-store, must-revalidate'); $action=$_GET['action']??'query'; $body=json_decode(file_get_contents('php://input'),true); if(!$body)$body=[]; $now=date('c'); echo json_encode(['ok'=>true,'action'=>$action,'data'=>['echo'=>$body,'message'=>'AJAX OK','time'=>$now],'code'=>200], JSON_UNESCAPED_UNICODE);